import React, { Component } from "react";
import BurgerMenu from "../assets/icons/burger-menu.png";

export default class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      click: false,
    };
  }

  clickMenu() {
    this.setState({
      click: !this.state.click,
    });
  }

  componentDidUpdate() {
    console.log(this.state.click);
  }
  render() {
    let showMenu;

    if (this.state.click) {
      showMenu = (
        <div
          className="m-auto border-2 rounded-b-md border-white shadow-lg shadow-slate-100 px-3 py-1 flex cursor-pointer"
          onClick={() => this.clickMenu()}
        >
          <img
            src={BurgerMenu}
            style={{ width: 30, height: 30 }}
            alt="icon menu"
          />
          <p className="m-auto text-black">Menu</p>
        </div>
      );
    } else {
      showMenu = (
        <div className="flex flex-col m-auto">
          <div
          className="m-auto border-2 rounded-b-md border-white shadow-lg shadow-slate-100 px-3 py-1 flex cursor-pointer"
          onClick={() => this.clickMenu()}
        >
          <p className="text-lg font-bold border-2 p-1 rounded-full">X</p>
          <p className="m-auto text-black">Hide Menu</p>
        </div>
          <p className="m-auto cursor-pointer">Home</p>
          <p className="m-auto cursor-pointer">Information</p>
          <p className="m-auto cursor-pointer">Marchandise</p>
          <p className="m-auto cursor-pointer">About</p>
        </div>
      );
    }

    return (
      <div className="flex flex-col max-w-sm m-auto">
        <div className="m-auto">
          <p className="font-extrabold text-black text-[20px] mt-5">Marmoot</p>
        </div>

        {showMenu}

      </div>
    );
  }
}
