import React, { Component } from "react";
import BurgerMenu from "../assets/icons/burger-menu.png";

export default class Header extends Component {

  constructor(props) {
    super(props)

    this.state = {
       click:false,
    }
  }

  clickMenu(){
    this.setState({
      click:!this.state.click
    })
  }

  componentDidUpdate(){
    console.log(this.state.click)
  }
  render(){

    let showMenu;

    if(this.state.click){
      showMenu =  <div className="m-auto border-2 rounded-b-md border-white shadow-lg shadow-slate-100 px-3 py-1 flex cursor-pointer" onClick={()=>this.clickMenu()}>
      <img
        src={BurgerMenu}
        style={{ width: 30, height: 30 }}
        alt="icon menu"
      />
      <p className="m-auto m-1 text-black">Menu</p>
    </div>
    }else{
      showMenu =   <div className="hidden">
      <p className="mx-3">Home</p>
      <p className="mx-3">Information</p>
      <p className="mx-3">Marchandise</p>
      <p className="mx-3">About</p>
    </div>
    }

    return (
      <div className="flex flex-col max-w-sm m-auto">
        <div className="m-auto">
          <p className="font-extrabold text-black text-[20px] mt-5">Marmoot</p>
        </div>

        <div className="m-auto border-2 rounded-b-md border-white shadow-lg shadow-slate-100 px-3 py-1 flex cursor-pointer" onClick={()=>this.clickMenu()}>
          <img
            src={BurgerMenu}
            style={{ width: 30, height: 30 }}
            alt="icon menu"
          />
          <p className="m-auto m-1 text-black">Menu</p>
        </div>



      </div>
    );
  }

}
